CREATE TABLE IF NOT EXISTS books (
    `author` VARCHAR(36) CHARACTER SET utf8,
    `bookid` INT,
    `category` VARCHAR(20) CHARACTER SET utf8,
    `isbn` VARCHAR(10) CHARACTER SET utf8,
    `pageCount` INT,
    `price` INT,
    `publishedDate` DATETIME,
    `shortDescription` VARCHAR(1930) CHARACTER SET utf8,
    `thumbnailUrl` VARCHAR(132) CHARACTER SET utf8,
    `title` VARCHAR(39) CHARACTER SET utf8
);


CREATE TABLE IF NOT EXISTS users (
    `firstname` VARCHAR(7) CHARACTER SET utf8,
    `lastname` VARCHAR(9) CHARACTER SET utf8,
    `userid` INT
);
